// AddStudentForm.js

import React, { useState } from 'react';
import axios from 'axios'; // Import Axios library for making HTTP requests
import { ToastContainer, toast } from 'react-toastify';

const AddstudentForm = () => {
  const [data, setFormData] = useState({
    first_name: '',
    last_name: '',
    gender: '',
  });


  const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev, [name]: value
        }));
    };
  const saveStudent =  (e) => {
    e.preventDefault();
     if (data.first_name.length === 0){
      toast.error("First Name is required", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000 
      })
    } else if (data.last_name.length === 0){
      toast.error("Last Name is required", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000
      })
    } else if (data.gender.length === 0){
      toast.error("Gender is required", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000
      })
    } else (

    // const token = sessionStorage.getItem("access_token");
    axios.post('http://localhost:4000/api/student/addStudent', data)
      .then(res => {
        toast.success('New student added successfully', {
          position: toast.POSITION.TOP_RIGHT,
          autoclose: 3000
        })
      }).catch(err => {
      toast.error('Something went wrong while adding the blog', {
          position: toast.POSITION.TOP_RIGHT,
          autoclose: 3000,
      })
    })
  )};


    return (
         <div className="w-full max-w-md mx-auto">
      <form onSubmit={saveStudent} className="bg-white shadow-2xl shadow-blue-500 rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="firstname">
            First Name
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="firstname"
            type="text"
            placeholder="First Name"
            name="first_name"
            value={data.firstname}
            onChange={handleChange}
            
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="lastname">
            Last Name
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="lastname"
            type="text"
            placeholder="Last Name"
            name="last_name"
            value={data.lastname}
            onChange={handleChange}
            
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="gender">
            Gender
          </label>
          <select
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="gender"
            name="gender"
            value={data.gender}
            onChange={handleChange}
            
          >
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="submit"
          >
            Submit
            </button>
          </div>
            <ToastContainer/>
      </form>
    </div>
  );
};

export default AddstudentForm;
